INSERT INTO `comment` VALUES (1, 45, 12, '这篇文章对于复杂的主题进行了简洁而深入的探讨，使读者能够快速获得重要信息。', '2023-06-12 15:45:47', 1, NULL);
INSERT INTO `comment` VALUES (2, 45, 13, '作者的文笔流畅且生动，通过精彩的描写和比喻，使文章更加生动有趣。', '2023-06-12 15:45:49', 1, NULL);
INSERT INTO `comment` VALUES (5, 45, 12, '这篇文章的研究方法全面而严谨，对问题进行了全面的分析和评估。', '2023-06-12 15:45:51', 1, NULL);
INSERT INTO `comment` VALUES (6, 45, 12, '作者在文章中展示了对于多学科知识的融汇和应用，提供了一个多维度的视角。', '2023-06-12 15:45:54', 1, NULL);
INSERT INTO `comment` VALUES (7, 45, 12, '我太喜欢了', '2023-06-12 15:45:56', 1, NULL);
INSERT INTO `comment` VALUES (8, 45, 12, '这篇文章非常有深度和洞察力，对于讨论的话题提供了全面的分析和观点。', '2023-06-12 15:45:58', 1, NULL);
INSERT INTO `comment` VALUES (9, 45, 12, '作者通过生动的描写和引人入胜的故事，成功地吸引了读者的注意力。', '2023-06-12 15:46:00', 1, NULL);
INSERT INTO `comment` VALUES (10, 45, 12, '文章的结构清晰，逻辑严密，让人容易理解和跟随作者的思路。', '2023-06-12 15:46:03', 1, NULL);
INSERT INTO `comment` VALUES (11, 45, 12, '作者通过详实的数据和研究结果，支持了自己的论点，增加了文章的可信度。', '2023-06-12 15:46:05', 1, NULL);
INSERT INTO `comment` VALUES (12, 45, 13, '这篇文章对于当前的社会问题提出了有价值的观点，并提供了一些创新性的解决方案。', '2023-06-12 15:46:07', 1, NULL);
INSERT INTO `comment` VALUES (13, 45, 13, '作者以客观的态度探讨了一个复杂的议题，展示了对多个立场的理解和尊重。', '2023-06-12 15:46:09', 1, NULL);
INSERT INTO `comment` VALUES (14, 45, 13, '文章的语言优美流畅，用词精准，给人一种愉悦的阅读体验。', '2023-06-12 15:46:11', 1, NULL);
INSERT INTO `comment` VALUES (15, 45, 13, '作者对于历史事件的回顾和分析非常透彻，使读者更好地理解了当时的背景和影响。', '2023-06-12 15:46:14', 1, NULL);
INSERT INTO `comment` VALUES (16, 45, 13, '这篇文章引发了我对于该主题的深思，激发了进一步的探索和研究的兴趣。', '2023-06-12 15:46:16', 1, NULL);
INSERT INTO `comment` VALUES (17, 48, 13, '作者在表达自己观点的同时，也给予了其他观点公正的讨论和对待。', '2023-06-12 15:46:18', 1, NULL);
INSERT INTO `comment` VALUES (18, 49, 13, '这篇文章的结论具有启发性和洞见，为读者提供了新的思考角度。', '2023-06-12 15:46:24', 1, NULL);
INSERT INTO `comment` VALUES (19, 50, 19, '作者通过生动的事例和个人经历，增加了文章的情感共鸣和人性化的元素。', '2023-06-12 15:46:27', 1, NULL);
INSERT INTO `comment` VALUES (20, 51, 19, '这篇文章的研究方法和实证分析非常严谨，使得结果更加可靠和可信。', '2023-06-12 15:46:29', 1, NULL);
INSERT INTO `comment` VALUES (21, 52, 19, '作者对于当下热门话题进行了深入的调查和调研，为读者提供了全面的信息。', '2023-06-12 15:46:31', 1, NULL);
INSERT INTO `comment` VALUES (22, 53, 19, '文章的观点鲜明，论证有力，让人信服。', '2023-06-12 15:46:33', 1, NULL);
INSERT INTO `comment` VALUES (23, 54, 19, '这篇文章的写作风格简洁明了，遣词造句得当，使得复杂的概念变得易于理解。', '2023-06-12 15:46:35', 1, NULL);
INSERT INTO `comment` VALUES (24, 55, 19, '作者在文章中融入了一些幽默和轻松的元素，增添了阅读的乐趣。', '2023-06-12 15:46:37', 1, NULL);
INSERT INTO `comment` VALUES (25, 56, 19, '这篇文章的思想深度超出了我对该主题的预期，让我对问题有了更全面的认识。', '2023-06-12 15:46:39', 1, NULL);
INSERT INTO `comment` VALUES (26, 57, 14, '作者的观点独到而有启发性，让人产生了更多的思考和讨论的欲望。', '2023-06-12 15:46:42', 1, NULL);
INSERT INTO `comment` VALUES (27, 58, 19, '这篇文章以深刻的洞察和独到的见解，为读者', '2023-06-12 15:46:44', 1, NULL);
INSERT INTO `comment` VALUES (28, 59, 19, '这篇文章内容丰富，观点独到，给人带来了全新的思考角度。', '2023-06-12 15:46:46', 1, NULL);
INSERT INTO `comment` VALUES (29, 60, 19, '作者在文章中展现了深厚的知识储备和对细节的把握能力，令人印象深刻。', '2023-06-12 15:46:49', 1, NULL);
INSERT INTO `comment` VALUES (30, 61, 19, '文章结构清晰，逻辑严密，使读者能够轻松跟随并理解作者的观点。', '2023-06-12 15:46:50', 1, NULL);
INSERT INTO `comment` VALUES (31, 62, 19, '作者对于当下热门话题的分析准确而深入，给人一种权威和可信的感觉。', '2023-06-12 15:46:52', 1, NULL);
INSERT INTO `comment` VALUES (32, 63, 14, '这篇文章的语言优美流畅，令人陶醉其中，让人愿意一直阅读下去。', '2023-06-12 15:46:54', 1, NULL);
INSERT INTO `comment` VALUES (33, 64, 14, '作者的观点充满思想深度，读后让人产生了很多反思和启发。', '2023-06-12 15:46:56', 1, NULL);
INSERT INTO `comment` VALUES (34, 65, 14, '文章通过生动的例子和引人入胜的叙述，将抽象的概念变得易于理解和接受。', '2023-06-12 15:46:58', 1, NULL);
INSERT INTO `comment` VALUES (35, 66, 14, '作者的研究方法和数据分析非常严谨，为文章的论证提供了坚实的支持。', '2023-06-12 15:47:00', 1, NULL);
INSERT INTO `comment` VALUES (36, 67, 14, '这篇文章的观点新颖独特，能够引发读者对话题的兴趣和讨论。', '2023-06-12 15:47:02', 1, NULL);
